    cubehelix_color = ->(gamma, s, r, h, p0, p1, x) do
      xg = x ** gamma
      a = h * xg * (1r - xg) / 2r
      phi = 2r * Math::PI * (s / 3r + r * x)
      xg + a * (p0 * Math.cos(phi) + p1 * Math.sin(phi))
    end

