    gnuplot_functions = {
      0  => ->(x) { 0r },
      1  => ->(x) { 0.5r },
      2  => ->(x) { 1r },
      3  => ->(x) { x },
      4  => ->(x) { x ** 2 },
      5  => ->(x) { x ** 3 },
