      11 => ->(x) { (x - 0.5).abs },
      12 => ->(x) { (2 * x - 1) ** 2 },
      13 => ->(x) { Math.sin(x * Math::PI) },
      14 => ->(x) { Math.cos(x * Math::PI).abs },
      15 => ->(x) { Math.sin(x * 2 * Math::PI) },
      16 => ->(x) { Math.cos(x * 2 * Math::PI) },
