      19 => ->(x) { Math.sin(x * 4 * Math::PI).abs },
      20 => ->(x) { Math.cos(x * 4 * Math::PI).abs },
      21 => ->(x) { 3 * x },
      22 => ->(x) { 3 * x - 1 },
      23 => ->(x) { 3 * x - 2 },
      24 => ->(x) { (3 * x - 1).abs },
      25 => ->(x) { (3 * x - 2).abs },
      26 => ->(x) { (3 * x - 1) / 2 },
      27 => ->(x) { (3 * x - 2) / 2 },
      28 => ->(x) { ((3 * x - 1) / 2).abs },
      29 => ->(x) { ((3 * x - 2) / 2).abs },
      30 => ->(x) { x / 0.32 - 0.78125 },
      31 => ->(x) { 2 * x - 0.84 },
      32 => ->(x) {
        case
        when x < 0.25
          4 * x
        when 0.25 <= x && x < 0.92
          -2 * x + 1.84
        when 0.92 <= x
          x / 0.08 - 11.5
        end
      },
      33 => ->(x) { (2 * x - 0.5).abs },
      34 => ->(x) { 2 * x },
      35 => ->(x) { 2 * x - 0.5 },
      36 => ->(x) { 2 * x - 1 },
    }

